sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (newValue === '') {
        return;
    }

    var requestedFor = g_form.getValue('u_requested_for');
    //g_form.addInfoMessage("requested for " + requestedFor);

    var ga = new GlideAjax('ClassGetManager');
    ga.addParam('sysparm_name', 'getManagerName');
    ga.addParam('sysparam_requestedFor', requestedFor);
    ga.getXML(PopulateManagerName);

    function PopulateManagerName(response) {
        var answer = response.responseXML.documentElement.getAttribute("answer");
        //g_form.addInfoMessage("answer " + answer);
        g_form.clearValue('u_manager');
        g_form.setValue('u_manager', answer);
    }

    if (isLoading  && !g_form.isNewRecord() || newValue === '' || newValue == oldValue) {
        return;
    }

    // Instantiate the GetEmailAddAndDept Script Include 
    var getEmailAddrAndDept = new GlideAjax('GetEmailAddAndDept');

    // Specify the getEmail method
    getEmailAddrAndDept.addParam('sysparm_name', 'getEmailnDept');

    // Pass the Requested for sys_id
    getEmailAddrAndDept.addParam('sysparm_userID', g_form.getValue('u_requested_for'));

    // Send the request to the server
    getEmailAddrAndDept.getXML(populateEmailAndDeptField);

    // When the response is back from the server
    function populateEmailAndDeptField(response) {
        // Extract the email address and department from the response, clear any value from the email field, 
        var answer = response.responseXML.documentElement.getAttribute("answer");

        var result = answer.evalJSON();

        g_form.clearValue('u_requested_for_email');
        g_form.clearValue('u_department');

        g_form.setValue('u_requested_for_email', result.userEmail);
        g_form.setValue('u_department', result.userDept);
    }

}
