sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
    initialize: function() {},
    isDateCurrentOrPast: function(strDate) {
        var rightNow = new GlideDateTime();
        var whenNeededDate = new GlideDateTime(strDate);

        var today = rightNow.getLocalDate();
        var isToday = whenNeededDate.getLocalDate();

        if (isToday.onOrBefore(today)) {
            return true;
        } else {
            return false;
        }
    },
    type: 'NeedItUtils'
};
