sys_script_include.script:
var GetEmailAddAndDept = Class.create();
GetEmailAddAndDept.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    // Define the getEmail function.  
    // Create a GlideRecord for the User table.
    // Use the sysparm_userID passed from the client side to retrieve a record from the User table.
    // Return the email address and department for the requested record
    getEmailnDept: function() {     		
        var userRecord = new GlideRecord("sys_user");
        userRecord.get(this.getParameter('sysparm_userID'));

		var userRecordObj = {};

		userRecordObj.userEmail = userRecord.getValue('email');
        userRecordObj.userDept = userRecord.getDisplayValue('department');

		return JSON.stringify(userRecordObj);
    },
    type: ' GetEmailAddAndDept'
});
